Ext.define('app.controller.paises', {
    extend: 'Ext.app.Controller',
    alias: 'controller.paises',
    refs: {
        paises: {
            autoCreate: true,
            selector: 'paises',
            xtype: 'paises'
        }
    },
    control: {
      
         "#paisesGrilla": {
            select: 'cargarEstados'
        },
        "#estadosGrilla": {
            select: 'cargarCiudades'
        }
    },
   
     cargarEstados: function (dv, record, item, index, e) {
        
        var id = record.data.id;
        this.getPaises().down("#paisSeleccionado").setValue(id);
        store_estados =  this.getPaises().down("#estadosGrilla").getStore();
        
        store_estados.load({
            params: {
                pais: id,
                tipoTransaccion: 'cargar'
            }
        });
        
        
  
      },
       cargarCiudades: function (dv, record, item, index, e) {
        
        var id = record.data.id;
        store_ciudades =  this.getPaises().down("#ciudadesGrilla").getStore();
        this.getPaises().down("#estadoSeleccionado").setValue(id);
        store_ciudades.load({
            params: {
                estado: id,
                tipoTransaccion: 'cargar'
            }
        });
        
        
  
      }

});
